function getTxtFileRus(filename) {
    (async () => {
        const text = await (await fetch("texts/" + filename + ".txt")).text();
        const textEng = await (await fetch("texts/" + filename + "_en.txt")).text();

        RusTextDiv.innerText = text;
        EngTextDiv.innerText = textEng;
    })();
};

function getTxtFileEng(filename) {
    (async () => {
        const text = await (await fetch("texts/" + filename + "_en.txt")).text();
        const textRus = await (await fetch("texts/" + filename + ".txt")).text();
       EngTextDiv.innerText = text;
       RusTextDiv.innerText = textRus;
    })();
};

const dropdownRus = document.getElementById('SelectRussian');
const dropdownEng = document.getElementById('SelectEnglish');

function syncDropdowns(event) {
    const selectedIndex = event.target.selectedIndex;
    if (event.target === dropdownRus) {
      dropdownEng.selectedIndex = selectedIndex;
    } else {
      dropdownRus.selectedIndex = selectedIndex;
    }
  }
  
  dropdownRus.addEventListener('change', syncDropdowns);
  dropdownEng.addEventListener('change', syncDropdowns);
  

  
  